pointCluster(data) {
            let res = [];
            let wgs = this.map.spatialReference;
            for (let val of data) {
                let pt = new this.mapObj.Point(val.lng, val.lat, wgs);

                // ÂùêÊ†áÁ≥ªËΩ¨Êç¢
                let webMercator = this.mapObj.webMercatorUtils.geographicToWebMercator(pt); 
                let attr = {
                    name: val.name
